exports.prevImg = (imgList, setImgObj, setImgIndex, imgIndex) => {
    // console.log('Previous => ', imgList, imgIndex);
    let imgIdx = imgIndex - 1;
    let totalItem = imgList.length - 1;
    if (imgIdx < 0) {
        imgIdx = totalItem;
        setImgObj(imgList[imgIdx]);
        setImgIndex(imgIdx);
    } else {
        setImgObj(imgList[imgIdx]);
        setImgIndex(imgIdx);
    }
}

exports.nextImg = (imgList, setImgObj, setImgIndex, imgIndex) => {
    // console.log('Next => ', imgList, imgIndex);
    let imgIdx = imgIndex + 1;
    let totalItem = imgList.length - 1;
    if (imgIdx > totalItem) {
        imgIdx = 0;
        setImgObj(imgList[imgIdx]);
        setImgIndex(imgIdx);
    } else {
        setImgObj(imgList[imgIdx]);
        setImgIndex(imgIdx);
    }
}
